import React from "react";
import './uploadNotes.css';
import $ from 'jquery';
export default function UploadNotes() {
    $('.dropdown-el').click(function(e) {
        e.preventDefault();
        e.stopPropagation();
        $(this).toggleClass('expanded');
        $('#'+$(e.target).attr('for')).prop('checked',true);
      });
      $(document).click(function() {
        $('.dropdown-el').removeClass('expanded');
      });
    return(

<div class="shade">
		<div class="blackboard">
				<form class="form" autocomplete="new-password">
						<p>
								<label>Name: </label>
								<input type="text" autocomplete="new-password"/>
						</p>
						<p>
								<label>Email: </label>
								<input type="text" autocomplete="new-password"/>
						</p>
						<p>
								<label>Phone: </label>
								<input type="tel" autocomplete="new-password"/>
						</p>
						<p>
								<label>Subject: </label>
                                <span class="dropdown-el">
    <input type="radio" name="sortType" value="Relevance" checked="checked" id="sort-relevance" ><label for="sort-relevance">Relevance</label></input>
    <input type="radio" name="sortType" value="Popularity" id="sort-best"><label for="sort-best" >Product Popularity</label></input>
    <input type="radio" name="sortType" value="PriceIncreasing" id="sort-low"><label for="sort-low" >Price Low to High</label></input>
    <input type="radio" name="sortType" value="PriceDecreasing" id="sort-high"><label for="sort-high" >Price High to Low</label></input>
    <input type="radio" name="sortType" value="ProductBrand" id="sort-brand"><label for="sort-brand" >Product Brand</label></input>
    <input type="radio" name="sortType" value="ProductName" id="sort-name"><label for="sort-name" >Product Name</label></input>
  </span>
						</p>
						<p>
								<label>Message: </label>
								<textarea></textarea>
						</p>
						<p class="wipeout">
								<input type="submit" value="Send" />
						</p>
				</form>
		</div>
</div>
    )
    }
